const usePageLayoutStyle = makeStyles(theme => ({
    root: {
        boxSizing: 'border-box',
        display: 'flex',
        flex: '1 0 auto',
        flexFlow: 'row wrap',
        justifyContent: 'flex-start',
        alignItems: 'flex-start',
