  {/*   <Route exact path="/cloudregister" component={CloudRegisterProperty} /> */}
    <Route exact path="/login" component={Login} />
    <ProtectedRoute exact path="/userprofile" >
      <UserProfile/>
      </ProtectedRoute>

  {/*   <Route exact path="/sess/:key/:val" component={Login} /> */}
  </Switch>
);

export default App;
